import React from "react";
import "./Signup.css";
import { useState } from "react";
import { addUser } from "../API/api.js";
const Signup = () => {
  // const [username, setUsername] = useState("");
  // const [email, setEmail] = useState("");
  // const [password, setPassword] = useState("");
  const [user, setUser] = useState({
    username: "",
    email: 0,
    password: "",   
  });

  const handleInputChange = (e) => {
    console.log(e.target.name);
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleRegister = async (e) => {
    e.preventDefault();

    const registerData = new RegisterData();
    registerData.append("UserName", username.Username);
    registerData.append("Email", email.Email);
    registerData.append("Password", password.password);
   

    await addUser(registerData);
  }



  return (
    <div className="Signup" onSubmit={handleRegister}>
      <h1>Sign Up</h1>
      <form>
        <input type="text"
            name="title"
            value={user.username}
            onChange={handleInputChange}
            className="AddInput1"
            required/>
        <input
          name="title"
          value={user.username}
          onChange={handleInputChange}
          className="AddInput1"
          required
          placeholder="Email"
        />
        <input
          name="title"
          value={user.username}
          onChange={handleInputChange}
          className="AddInput1"
          required
        />
        <input
          name="title"
          value={user.username}
          onChange={handleInputChange}
          className="AddInput1"
          required
          placeholder="Confirm password"
        />

        <button className="button" type="submit">
          Sign Up
        </button>
      </form>
    </div>
  );
};
export default Signup;
