import React from "react";
import "./Signup.css";
import { useState } from "react";
import { addUser } from "../API/api.js";
const Signup = () => {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const [productName, setProductName] = useState('');
  const [productDescription, setProductDescription] = useState('');
  const [productImage, setProductImage] = useState('');
  const [productPrice, setProductPrice] = useState('');
  const [productType, setProductType] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();

    const UserData = {
      username: username,
      email: email,
      password: password,
    };

    AddProduct(productData);
  };


  const handleInputChange = (e) => {
    console.log(e.target.name);
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleRegister = async (e) => {
    e.preventDefault();

    const registerData = new RegisterData();
    registerData.append("UserName", user.username);
    registerData.append("Email", user.email);
    registerData.append("Password", user.password);
   

    await addUser(registerData);
  }



  return (
    <div className="Signup" onSubmit={handleRegister}>
      <h1>Sign Up</h1>
      <form>
        <input type="text"
            name="username"
            value={user.username}
            onChange={(e) => setProductName(e.target.value)}            className="AddInput1"
            required/>
        <input
        type="Email"
          name="Email"
          value={user.email}
          onChange={handleInputChange}
          className="AddInput1"
          required
          placeholder="Email"
        />
        <input
          name="password"
          value={user.password}
          onChange={handleInputChange}
          className="AddInput1"
          required
        />
        <input
          name="password"
          value={user.password}
          onChange={handleInputChange}
          className="AddInput1"
          required
          placeholder="Confirm password"
        />

        <button className="button" type="submit">
          Sign Up
        </button>
      </form>
    </div>
  );
};
export default Signup;
