import React from "react";
import "./Signup.css";
import { useState } from "react";
import { addUser } from "../API/api.js";
import axios from "axios";
const Signup = () => {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");


  const handleSubmit = (event) => {
    event.preventDefault();

    const UserData = {
      username: username,
      email: email,
      password: password,
    };

    addUser(UserData);
  };


  const handleInputChange = (e) => {
    console.log(e.target.name);
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleRegister = async (e) => {
    e.preventDefault();

    const registerData = new RegisterData();
    registerData.append("UserName", user.username);
    registerData.append("Email", user.email);
    registerData.append("Password", user.password);
   

    await addUser(registerData);
  }



  return (
    <div className="Signup" onSubmit={handleSubmit}>
      <h1>Sign Up</h1>
      <form>
        <input type="text"
            name="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}            
            className="AddInput1"
            required/>
        <input
        type="Email"
          name="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}            
          className="AddInput1"
          required
          placeholder="Email"
        />
        <input
          name="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}            
          className="AddInput1"
          required
        />
        <input
          name="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}            
          className="AddInput1"
          required
          placeholder="Confirm password"
        />

        <button className="button" type="submit">
          Sign Up
        </button>
      </form>
    </div>
  );
};
export default Signup;
